        })
    }

    return (
        <HeaderContainer>
            <LogoContainer>
                <Link to={`/`} >
                    <Logo
                        src={logo}
                        alt="Logo"
                    />
                </Link>
            </LogoContainer>
            <RightContainer>
                {userData? (
                        <LoginButton to={`/login/`} onClick={()=> handleLogout() } >Logout</LoginButton>
                    ) : (
                        <LoginButton to={`/login/`}>Login</LoginButton>
                    )
                }
            </RightContainer>
        </HeaderContainer>
    );
}

const HeaderContainer = styled.div`
    width: 90%;
    margin: 0 auto;
    padding: 30px 0;
    display: flex;
    justify-content: space-between;
`;
const LogoContainer = styled.div`
    width: 65px;
`
const Logo = styled.img`
    width: 100%;
    display: block;
`;
const RightContainer = styled.div`
    display: flex;
    align-items: center;
`;
const LoginButton = styled(Link)`
    background: #046bf6;
    border-radius: 4px;
    padding: 13px 45px;
    color: #fff;
    font-size: 18px;
    border-radius: 4px;
    font-weight: bold;
    text-decoration:none;
`;

export default Header;
